import com.eviware.soapui.model.testsuite.TestRunner.Status
def tc = context.testCase.testSuite.project.testSuites["Customer_Repo"].testCases["TC_01_CustomerRepo_Prop Expanstion"]

def tc1 = context.testCase
def email = tc1.testSteps["Properties"].getPropertyValue("email")
def firstname = tc1.testSteps["Properties"].getPropertyValue("firstname")
def lastname = tc1.testSteps["Properties"].getPropertyValue("lastname")

tc.testSteps["Properties"].setPropertyValue("email", email)
tc.testSteps["Properties"].setPropertyValue("firstname", firstname)
tc.testSteps["Properties"].setPropertyValue("lastname", lastname)

def runner = tc.run(null,false)
log.info "Status =$runner.status, time taken to complete the execution is $runner.timeTaken ms"
log.info "number of test steps executed are "+runner.results.size()

import jxl.*
import com.eviware.soapui.model.testsuite.TestRunner.Status
def tc=context.testCase.testSuite.project.testSuites["Customer_Repo"].testCases["TC_01_CustomerRepo_Prop Expanstion"]
File myfile= new File("C:\\Users\\SmritikonaSukul\\Documents\\SDETLP8\\Cust_Repo.xls")
Workbook wb = Workbook.getWorkbook(myfile)
Sheet sh= wb.getSheet("Sheet1")
int rc=sh.getRows();
log.info "total number of rows containing data are $rc"
for(int i=1;i<rc;i++)
{
    def email=sh.getCell(0,i).getContents()
    def firstname=sh.getCell(1,i).getContents()
    def lastname=sh.getCell(2,i).getContents()
    log.info "current username is $email"
    log.info "current password is $firstname"
    log.info "current password is $lastname"
    tc.testSteps["Properties"].setPropertyValue("email",email)
    tc.testSteps["Properties"].setPropertyValue("firstname",firstname)
    tc.testSteps["Properties"].setPropertyValue("lastname",lastname)
    def runner=tc.run(null,false)
    log.info "Status =$runner.status, time taken to complete the execution is $runner.timeTaken ms"
    log.info "number of test steps executed are "+runner.results.size()
}
